import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000',  // Ensure this is the correct backend URL
  withCredentials: true,             // Allows cookies to be sent and received
  headers: {
    'Content-Type': 'application/json',
  },
});

export default API;
